import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useCart } from '../context/CartContext';
import { 
  UserCircleIcon,
  ShoppingBagIcon,
  DocumentTextIcon 
} from '@heroicons/react/24/outline';

const Navbar = () => {
  const { user, logout } = useAuth();
  const { cart } = useCart();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const cartItemsCount = cart.reduce((count, item) => count + item.quantity, 0);

  return (
    <nav className="bg-white shadow-lg">
      <div className="container mx-auto px-4">
        <div className="flex justify-between items-center h-16">
          <Link to="/" className="flex items-center">
            <img src="/logo.jpg" alt="Emporium" className="h-8 w-8 mr-2"/>
              <span className="text-2xl font-bold text-G-600">
                  Emporium
              </span>
          </Link>

          <div className="flex items-center space-x-6">
            <Link to="/" className="text-gray-700 hover:text-blue-600">
              Home
            </Link>
            <Link to="/products" className="text-gray-700 hover:text-blue-600">
              Products
            </Link>
            
            {user?.isAdmin && (
              <Link to="/admin" className="text-gray-700 hover:text-blue-600">
                Admin Panel
              </Link>
            )}

            <Link to="/cart" className="relative">
              {/* Replace Heroicon with text/SVG */}
              <span className="text-gray-700 hover:text-blue-600">ðŸ›’</span>
              {cartItemsCount > 0 && (
                <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                  {cartItemsCount}
                </span>
              )}
            </Link>

            {user ? (
                <div className="flex items-center space-x-4">
                  <Link to="/profile" className="text-gray-700 hover:text-blue-600">
                    <UserCircleIcon className="h-6 w-6" />
                  </Link>
                  <Link to="/order-history" className="text-gray-700 hover:text-blue-600">
                    <DocumentTextIcon className="h-6 w-6" />
                  </Link>
                  <Link to="/cart" className="relative">
                    <ShoppingBagIcon className="h-6 w-6 text-gray-700 hover:text-blue-600" />
                    {cartItemsCount > 0 && (
                      <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                        {cartItemsCount}
                      </span>
                    )}
                  </Link>
                  <span className="text-gray-700">
                    {user.name}
                  </span>
                  <button
                    onClick={handleLogout}
                    className="btn-secondary text-sm"
                  >
                    Logout
                  </button>
                </div>
              ) : (
                <Link to="/login" className="btn-primary text-sm">
                  Login
                </Link>
              )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
