import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { 
  ShoppingCartIcon, 
  UserIcon,
  SunIcon,
  MoonIcon,
  UserCircleIcon,
  DocumentTextIcon,
  Bars3Icon,
  XMarkIcon
} from '@heroicons/react/24/outline';
import { useAuth } from '../context/AuthContext';
import { useCart } from '../context/CartContext';

const Navbar = () => {
  const { user, logout } = useAuth();
  const { cart } = useCart();
  const navigate = useNavigate();
  const [darkMode, setDarkMode] = React.useState(true);
  const [mobileMenuOpen, setMobileMenuOpen] = React.useState(false);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const cartItemsCount = cart.reduce((count, item) => count + item.quantity, 0);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    const html = document.documentElement;
    if (darkMode) {
      html.classList.remove('dark');
    } else {
      html.classList.add('dark');
    }
  };

  return (
    <nav className="bg-dark-card border-b border-dark-border shadow-darker sticky top-0 z-40">
      <div className="container mx-auto px-4">
        <div className="flex justify-between items-center h-16">
          {/* Logo */}
          <Link to="/" className="flex items-center space-x-2 group">
            <div className="w-8 h-8 bg-gradient-to-br from-primary to-secondary rounded-lg flex items-center justify-center group-hover:scale-110 transition-transform duration-300">
              <span className="text-white font-bold text-lg">E</span>
            </div>
            <span className="text-xl md:text-2xl font-bold bg-gradient-to-r from-primary to-secondary bg-clip-text text-transparent">
              EMPORIUM
            </span>
          </Link>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center space-x-4">
            <Link to="/" className="nav-link px-3 py-2 rounded-lg hover:bg-dark-border transition-colors">
              Home
            </Link>
            <Link to="/products" className="nav-link px-3 py-2 rounded-lg hover:bg-dark-border transition-colors">
              Products
            </Link>
            
            {user?.isAdmin && (
              <Link to="/admin" className="nav-link px-3 py-2 rounded-lg hover:bg-dark-border transition-colors">
                Admin Panel
              </Link>
            )}

            <button
              onClick={toggleDarkMode}
              className="p-2 rounded-lg bg-dark-border hover:bg-slate-700 transition-colors"
              title={darkMode ? "Switch to light mode" : "Switch to dark mode"}
            >
              {darkMode ? (
                <SunIcon className="h-5 w-5 text-yellow-500" />
              ) : (
                <MoonIcon className="h-5 w-5 text-gray-400" />
              )}
            </button>

            {user && (
              <>
                <Link to="/profile" className="p-2 rounded-lg hover:bg-dark-border transition-colors" title="Profile">
                  <UserCircleIcon className="h-5 w-5 text-gray-300" />
                </Link>
                <Link to="/order-history" className="p-2 rounded-lg hover:bg-dark-border transition-colors" title="Orders">
                  <DocumentTextIcon className="h-5 w-5 text-gray-300" />
                </Link>
              </>
            )}

            <Link to="/cart" className="relative p-2 rounded-lg hover:bg-dark-border transition-colors" title="Cart">
              <ShoppingCartIcon className="h-5 w-5 text-gray-300" />
              {cartItemsCount > 0 && (
                <span className="absolute -top-1 -right-1 bg-primary text-white text-xs rounded-full h-5 w-5 flex items-center justify-center font-bold neon-glow">
                  {cartItemsCount}
                </span>
              )}
            </Link>

            {user ? (
              <div className="flex items-center space-x-2">
                <span className="text-gray-300 text-sm">
                  Hi, {user.name.split(' ')[0]}
                </span>
                <button
                  onClick={handleLogout}
                  className="btn-secondary text-sm px-3 py-1"
                >
                  Logout
                </button>
              </div>
            ) : (
              <Link to="/login" className="btn-primary text-sm">
                Login
              </Link>
            )}
          </div>

          {/* Mobile Menu Button & Cart */}
          <div className="md:hidden flex items-center space-x-2">
            <Link to="/cart" className="relative p-2 rounded-lg hover:bg-dark-border transition-colors">
              <ShoppingCartIcon className="h-5 w-5 text-gray-300" />
              {cartItemsCount > 0 && (
                <span className="absolute -top-1 -right-1 bg-primary text-white text-xs rounded-full h-5 w-5 flex items-center justify-center font-bold neon-glow">
                  {cartItemsCount}
                </span>
              )}
            </Link>
            <button
              onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
              className="p-2 rounded-lg hover:bg-dark-border transition-colors"
            >
              {mobileMenuOpen ? (
                <XMarkIcon className="h-6 w-6 text-gray-300" />
              ) : (
                <Bars3Icon className="h-6 w-6 text-gray-300" />
              )}
            </button>
          </div>
        </div>

        {/* Mobile Menu */}
        {mobileMenuOpen && (
          <div className="md:hidden border-t border-dark-border bg-dark-card">
            <div className="px-2 pt-2 pb-3 space-y-1">
              <Link 
                to="/" 
                className="block px-3 py-2 rounded-lg hover:bg-dark-border transition-colors nav-link"
                onClick={() => setMobileMenuOpen(false)}
              >
                Home
              </Link>
              <Link 
                to="/products" 
                className="block px-3 py-2 rounded-lg hover:bg-dark-border transition-colors nav-link"
                onClick={() => setMobileMenuOpen(false)}
              >
                Products
              </Link>
              
              {user?.isAdmin && (
                <Link 
                  to="/admin" 
                  className="block px-3 py-2 rounded-lg hover:bg-dark-border transition-colors nav-link"
                  onClick={() => setMobileMenuOpen(false)}
                >
                  Admin Panel
                </Link>
              )}

              {user && (
                <>
                  <Link 
                    to="/profile" 
                    className="block px-3 py-2 rounded-lg hover:bg-dark-border transition-colors nav-link"
                    onClick={() => setMobileMenuOpen(false)}
                  >
                    Profile
                  </Link>
                  <Link 
                    to="/order-history" 
                    className="block px-3 py-2 rounded-lg hover:bg-dark-border transition-colors nav-link"
                    onClick={() => setMobileMenuOpen(false)}
                  >
                    Order History
                  </Link>
                </>
              )}

              <div className="flex items-center justify-between px-3 py-2">
                <span className="text-gray-300 text-sm">Dark Mode</span>
                <button
                  onClick={toggleDarkMode}
                  className="p-2 rounded-lg bg-dark-border hover:bg-slate-700 transition-colors"
                >
                  {darkMode ? (
                    <SunIcon className="h-5 w-5 text-yellow-500" />
                  ) : (
                    <MoonIcon className="h-5 w-5 text-gray-400" />
                  )}
                </button>
              </div>

              {user ? (
                <div className="px-3 py-2 space-y-2">
                  <div className="text-gray-300 text-sm">
                    Hi, {user.name.split(' ')[0]}
                  </div>
                  <button
                    onClick={() => {
                      handleLogout();
                      setMobileMenuOpen(false);
                    }}
                    className="w-full btn-secondary text-sm px-3 py-2"
                  >
                    Logout
                  </button>
                </div>
              ) : (
                <div className="px-3 py-2">
                  <Link 
                    to="/login" 
                    className="block w-full btn-primary text-sm text-center py-2"
                    onClick={() => setMobileMenuOpen(false)}
                  >
                    Login
                  </Link>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
